import React from 'react';
import './shop.styles.scss';
import CollectionOverview from '../../components/collection-overview/collection-overview.component';
import CollectionPage from '../collection/collection.component';
import WithSpinner from '../../components/with-spinner/with-spinner.component';

import {Route} from 'react-router-dom';

import {connect} from 'react-redux';
import {fetchCollectionsStartAsync} from '../../redux/shop/shop.actions';
import {selectIsFetchingCollections} from '../../redux/shop/shop.selectors';

import {createStructuredSelector} from 'reselect';


const CollectionOverviewWithSpinner = WithSpinner(CollectionOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);


class ShopPage extends React.Component{
   
    componentDidMount(){
        
    }
    render(){
        console.log(this.state)
        const {match}=this.props;
        const {isFetching}=this.props;
        return(
            <div className='shop-page'>
                <Route exact path={match.path}
                 render={(props)=> <CollectionOverviewWithSpinner isLoading={isFetching} {...props} />} /> 
                <Route path={`${match.path}/:collectionId`} 
                 render={(props)=> <CollectionPageWithSpinner isLoading={isFetching} {...props} />} />
            </div>
        )
    }
}

const mapStateToProps = createStructuredSelector({
    isFetching: selectIsFetchingCollections
})


const mapDispatchToProps = dispatch =>({
    updateCollections: dispatch=>dispatch(fetchCollectionsStartAsync())
})



export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
