import {takeLatest, call, all, put} from 'redux-saga/effects';
import userActionTypes from './user.types'
import {
    signInSuccess,
    signInFailure
    } from './user.actions'
    
import {
    auth,
    googleProvider,
    createUserProfileDocument
} from '../../firebase/firebase.utils'

function* signInWithGoogle(){
    try {
        const userAuth =yield auth.signInWithPopup(googleProvider);
        const {user} = userAuth;
        const userRef=yield call(createUserProfileDocument, user);
        const userSnapshot =yield userRef.get();

        const userData = {id: userRef.id, ...userSnapshot.data()};

        yield put(signInSuccess(userData));

    } catch (error) {
        yield put(signInFailure(error.message));
    }

}

function* signInWithEmail(){
    const userAuth =yield auth.signInWithEmailAndPassword()

}


function* googleSignInStart(){
    yield takeLatest(userActionTypes.GOOGLE_SIGN_IN_START, signInWithGoogle)
}

function* emailSignInStart(){
    yield takeLatest(userActionTypes.EMAIL_SIGN_IN_START, signInWithEmail )
}


function* userSagas(){
    yield all([
        call(googleSignInStart),
        call(emailSignInStart)
    ])
}

export default userSagas;
