import {takeEvery, call, put} from 'redux-saga/effects';
import shopActionTypes from './shop.types';
import {fetchCollectionsSuccess,
        fetchCollectionsFailure} from './shop.actions'

import {firestore, convertCollectionsSnapshotToMap} from '../../firebase/firebase.utils';

function* fetchCollectionsAsync(){
    try {
        const collectionsRef =yield firestore.collection('collections');
        const snapshot = yield collectionsRef.get();
        const collection = yield cconvertCollectionsSnapshotToMap(snapshot);
        
        put(fetchCollectionsSuccess(collection));
    } catch (error) {
        put(fetchCollectionsFailure(error.message))
    }
}

export function* fetchCollectionsStart(){
    yield takeEvery( shopActionTypes.FETCH_COLLECTIONS_START,fetchCollectionsAsync)
}
