import {takeLatest, call, all, put} from 'redux-saga/effects';
import userActionTypes from './user.types'
import {
    googleSignInSuccess,
    googleSignInFailure
    
} from './user.actions'
import {
    auth,
    googleProvider,
    createUserProfileDocument
} from '../../firebase/firebase.utils'

function* googleSignInAsync(){
    try {
        const userAuth =yield auth.signInWithPopup(googleProvider);
        const {user} = userAuth;
        const userRef=yield createUserProfileDocument(user);
        const userSnapshot =yield userRef.get();

        const userData = {id: userRef.id, ...userSnapshot.data()};

        yield put(call(googleSignInSuccess, userData))

    } catch (error) {
        yield put(call(googleSignInFailure, error.message))
    }

}


function* googleSignInStart(){
    yield takeLatest(userActionTypes.GOOGLE_SIGN_IN_START, googleSignInAsync)
}


function* userSagas(){
    yield all([call(googleSignInStart)])
}

export default userSagas;
